<?phpdate_default_timezone_set('Asia/Manila');include "func.php";class db extends functions{    // The database connection    protected static $connection;	    /**     * Connect to the database     *      * @return bool false on failure / mysqli MySQLi object instance on success     */    public function connect() {            // Try and connect to the database        if(!isset(self::$connection)) {            // Load configuration as an array. Use the actual location of your configuration file		$DB_host = "localhost";		$DB_user = "gabby";		$DB_pass = "2023";		$DB_name = "gabby";	            self::$connection = new MySQLi($DB_host,$DB_user,$DB_pass,$DB_name);        }        // If connection was not successful, handle the error        if(self::$connection === false) {            // Handle error - notify administrator, log to a file, show an error screen, etc.            return false;        }        return self::$connection;    }	/**     * Fetch the last error from the database     *      * @return string Database error message     */    public function error() {        $connection = $this -> connect();        return $connection -> error;    }		}?>